mapper.select:
SELECT ID, FREE_POST_TITLE, FREE_POST_CONTENT, FREE_POST_CREATE_DATE, LIKE_COUNT, REPLY_COUNT, CREATE_DATE, UPDATE_DATE, USER_ID
FROM TBL_FREE_POST
WHERE ID = #{id}

SELECT ROWNUM, ID, FREE_POST_TITLE, FREE_POST_CONTENT, FREE_POST_CREATE_DATE, LIKE_COUNT, REPLY_COUNT, CREATE_DATE, UPDATE_DATE, USER_ID FROM
    (
        SELECT ROWNUM R, ID, FREE_POST_TITLE, FREE_POST_CONTENT, FREE_POST_CREATE_DATE, LIKE_COUNT, REPLY_COUNT, CREATE_DATE, UPDATE_DATE, USER_ID
        FROM
            (
                SELECT ID, FREE_POST_TITLE, FREE_POST_CONTENT, FREE_POST_CREATE_DATE, LIKE_COUNT, REPLY_COUNT, CREATE_DATE, UPDATE_DATE, USER_ID
                FROM TBL_FREE_POST
                ORDER BY ID DESC
            ) P1

        WHERE ROWNUM <= #{pagination.endRow}
          and FREE_POST_TITLE  LIKE '%' || #{keyword} || '%'
    ) P2
WHERE P2.R >= #{pagination.startRow}

mapper.update:
UPDATE TBL_FREE_POST
SET FREE_POST_TITLE = #{freePostTitle}, FREE_POST_CONTENT = #{freePostContent} , UPDATE_DATE = TO_DATE(CURRENT_DATE)
WHERE ID = #{id}

UPDATE TBL_FREE_POST
SET LIKE_COUNT = LIKE_COUNT + 1
WHERE ID = #{id}

UPDATE TBL_FREE_POST
SET LIKE_COUNT = LIKE_COUNT - 1
WHERE ID = #{id}

UPDATE TBL_FREE_POST
SET REPLY_COUNT = REPLY_COUNT + 1
WHERE ID = #{id}

UPDATE TBL_FREE_POST
SET REPLY_COUNT = REPLY_COUNT - 1
WHERE ID = #{id}

UPDATE TBL_FREE_POST_REPLY
SET F_POST_REPLY_CONTENT = #{freePostReplyContent}, UPDATE_DATE = CURRENT_TIMESTAMP
WHERE ID = #{id}

UPDATE TBL_FREE_POST
SET LIKE_COUNT = LIKE_COUNT + 1
WHERE ID = #{id}

UPDATE TBL_FREE_POST
SET LIKE_COUNT = LIKE_COUNT - 1
WHERE ID = #{id}
